CREATE TABLE `?:checkout_question_descriptions` (
    `question_id` mediumint(8) unsigned NOT NULL default 0,
    `title` mediumtext,
    `lang_code` char(2) NOT NULL default '',
    PRIMARY KEY (`question_id`, `lang_code`)
    ) Engine=MyISAM DEFAULT CHARSET UTF8;

CREATE TABLE `?:checkout_question_variants` (
    `variant_id` mediumint(8) unsigned NOT NULL auto_increment,
    `question_id` mediumint(8) unsigned NOT NULL default 0,
    `position` smallint(5) unsigned NOT NULL default '0',
    PRIMARY KEY (`variant_id`)
) Engine=MyISAM DEFAULT CHARSET UTF8;

CREATE TABLE `?:checkout_question_variant_descriptions` (
    `variant_id` mediumint(8) unsigned NOT NULL default 0,
    `title` mediumtext,
    `lang_code` char(2) NOT NULL default '',
    PRIMARY KEY (`variant_id`, `lang_code`)
    ) Engine=MyISAM DEFAULT CHARSET UTF8;
